css:
@media (min-width: 1200px) {
        .atable .container {
            width: 970px;
        }
    }

    .atable .print-heading h2 {
        font-size: 20px;
        font-family: Calibri;
        color: #00a4ad;
    }

    .atable .letter-block {
        margin-bottom: 30px;
    }

    .atable .letter-block p {
        margin: 0 0 2px;
        font-size: 11px;
    }

    .atable .col-xs-1,
    .atable .col-sm-1,
    .atable .col-md-1,
    .atable .col-lg-1,
    .atable .col-xs-2,
    .atable .col-sm-2,
    .atable .col-md-2,
    .atable .col-lg-2,
    .atable .col-xs-3,
    .atable .col-sm-3,
    .atable .col-md-3,
    .atable .col-lg-3,
    .atable .col-xs-4,
    .atable .col-sm-4,
    .atable .col-md-4,
    .atable .col-lg-4,
    .atable .col-xs-5,
    .atable .col-sm-5,
    .atable .col-md-5,
    .atable .col-lg-5,
    .atable .col-xs-6,
    .atable .col-sm-6,
    .atable .col-md-6,
    .atable .col-lg-6,
    .atable .col-xs-7,
    .atable .col-sm-7,
    .atable .col-md-7,
    .atable .col-lg-7,
    .atable .col-xs-8,
    .atable .col-sm-8,
    .atable .col-md-8,
    .atable .col-lg-8,
    .atable .col-xs-9,
    .atable .col-sm-9,
    .atable .col-md-9,
    .atable .col-lg-9,
    .atable .col-xs-10,
    .atable .col-sm-10,
    .atable .col-md-10,
    .atable .col-lg-10,
    .atable .col-xs-11,
    .atable .col-sm-11,
    .atable .col-md-11,
    .atable .col-lg-11,
    .atable .col-xs-12,
    .atable .col-sm-12,
    .atable .col-md-12,
    .atable .col-lg-12 {
        padding-right: 0px;
        padding-left: 0px;
    }

    table,
    td{
        border-collapse: collapse;
        border-color: #95969A !important;
        font-size: 13px;

    }

     th {
        border-collapse: collapse;
        border-color: #95969A !important;
        font-size: 13px;
        color:#fff;

    }

    .atable .row {
        margin-right: 0px;
        margin-left: 0px;
    }

    .print-format th {
        background-color: #00a4ad!important;
        font-weight: 400;
    }

    .atable .col-print-4 {
        width: 33.3333333%;
        float: left;
    }

    .atable .pulled {
        margin-top: -41px !important;
    }

    .atable .col-print-offset-8 {
        float: right;
        width: 33.33333333%;
    }
    .atable .col-print-offset-6 {
        float: right;
        width: 50%;
    }
    .atable .bordered{
        border-top: 1px solid #00a4ad;
        font-style: italic;
        font-size: 12px;
        color: #00a4ad;
    }
