css:
.textcenter{
        text-align:center;
}
.borderclass{
    border-bottom: 1px solid dodgerblue;
}
table {
    width:100%;
    text-align:left;
    border-collapse: collapse;
}

table, td, th {
    /*width:50%;*/
    border: 1px solid dodgerblue;
    font-size:10px;

}
.print-format{
    padding: 12px !important;
    margin-bottom: 0px;
    margin-top:0px;
}

html:
{% set address %}{% endset %}
{% if doc.billing_address %}
    {%- set address=frappe.get_doc("Address",doc.billing_address) -%}
{% endif %}

{% set b_address %}{% endset %}
{% if doc.supplier_address %}
    {%- set b_address=frappe.get_doc("Address",doc.supplier_address) -%}
{% endif %}

{% set s_address %}{% endset %}
{% if doc.shipping_address %}
    {% set s_address=frappe.get_doc("Address",doc.shipping_address) %}
{% endif %}

{% set disc_col=[] %}
{% for disc_per in doc.items %}
    {% if disc_per.discount_percentage %}
        {% set disc_col = disc_col.append(disc_per.discount_percentage) %}
    {% endif %}
{% endfor %}

{% if doc.additional_discount_percentage %}
    {% set rowspan = doc.taxes|length + 3 %}
{% else %}
    {% set rowspan = doc.taxes|length + 2 %}
{% endif %}

<!DOCTYPE HTML>
<table>
    <tr>
        <td>
            {% if letter_head %}
                {{ letter_head }}
            {% endif %}
        </td>
    </tr>
</table>
<table>
    <tr>
        <td style="text-align: center; font-size: 12px;"><b>PURCHASE ORDER</b></td>
    </tr>
</table>
<div style="width:100%;font-size: 15px;">
    <table>
        <tr>
            <td style="width: 50%;">
                <div class="row">
                    <div class="col-xs-7">
                        <b>{{ _("PO No.") }}:</b>&nbsp; <span style="font-size: 10px"><b>
                        {{ doc.name }}</b></span>
                    </div>
                    <div class="col-xs-5">
                        <b>{{ _("PO Date") }}:</b>&nbsp; {{ doc.get_formatted("transaction_date") }}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-7">
                        <b>{{ _("QTN No.") }}:</b>&nbsp;
                        {% if doc.supplier_quotation_no %}
                            {{doc.supplier_quotation_no}}
                        {% endif %}
                    </div>
                    <div class="col-xs-5">
                        <b>{{ _("QTN Date") }}:</b>&nbsp;
                        {% if doc.supplier_quotation_date %}
                            {{doc.get_formatted("supplier_quotation_date")}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-7">
                        <b>{{ _("State") }}:</b>&nbsp;
                        {% if address.gst_state %}
                            {{address.gst_state}}
                        {% endif %}
                    </div>
                    <div class="col-xs-5">
                        <b>{{ _("State Code") }}:</b>&nbsp;
                        {% if doc.company_gstin %}
                            {{doc.company_gstin[0:2]}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-7">
                        <b>{{ _("GSTIN") }}:</b>&nbsp;
                        {% if doc.company_gstin %}
                            {{doc.company_gstin}}
                        {% endif %}
                    </div>
                    <div class="col-xs-5">
                        <b>{{ _("PAN") }}:</b>&nbsp;
                        {% if doc.company_gstin %}
                            {{doc.company_gstin[2:-3]}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("Our Contact Person") }}: </b>&nbsp;
                        {% if doc.our_contact_person %} 
                            {{ doc.our_contact_person }} 
                        {% endif %}
                    </div>
                </div>
            </td>
            <td style="width: 50%;">
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("Transporter Name") }}: </b>&nbsp;
                        {% if doc.transporter_name %} 
                            {{ doc.transporter_name }} 
                        {% endif %}
                    </div>

                </div>
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("Delivery At") }}:</b>&nbsp;
                        {% if doc.destination %} 
                            {{doc.destination}} 
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("Freight") }}: </b>&nbsp; 
                        {% if doc.freight %} 
                            {{doc.freight}} 
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("Insurance") }}:</b>&nbsp; 
                        {% if doc.insurance_ %} 
                            {{doc.insurance_}} 
                        {% endif %}                       
                    </div>
                </div>
            </td>
        </tr>
</table>

<table>
        <tr>
            <td class="text-center" style="width: 50%;">
                <b>{{ _("Details of Supplier")}}</b>
            </td>
            <td class="text-center" style="width: 50%;">
                <b>{{ _("Details of Consignee")}}</b>
            </td>
        </tr>
        <tr style="width: 50%;">

            <td>
                <b>{{ _("Supplier Name") }}:&nbsp;
                {% if b_address.address_title %}
                    {{b_address.address_title}}
                {% endif %} </b><br>
                <b>{{ _("Address") }}:</b>&nbsp; 
                {% if b_address.address_line1 %} 
                    {{b_address.address_line1}}, 
                {% endif %}<br>
                {% if b_address.address_line2 %} 
                    {{b_address.address_line2}} ,
                {% endif %}<br>
                {% if b_address.city %} 
                    {{b_address.city}} 
                {% endif %}
                {% if b_address.pincode %} 
                    -{{b_address.pincode}}, 
                {% endif %}
                {% if b_address.gst_state %} 
                    {{b_address.gst_state}}, 
                {% endif %}
                {% if b_address.country %} 
                    {{b_address.country}}.
                {% endif %}<br>

                <b>Contact Person:</b>&nbsp;
                {% if doc.contact_display %}
                    {{doc.contact_display}}&nbsp;&nbsp;&nbsp;
                {% endif %}
                <b>M.No.:</b>&nbsp;
                {% if doc.contact_mobile %}
                    {{doc.contact_mobile}}
                {% endif %}<br>
                <b>Email:</b>&nbsp;
                {% if doc.contact_email %}
                    {{doc.contact_email}} 
                {% endif %}<br>
                <b>{{ _("GSTIN") }}:</b>&nbsp;
                {% if b_address.gstin %}
                    {{b_address.gstin}}
                {% endif %}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                <b>{{ _("State Code") }}:</b>&nbsp;
                {% if b_address.gst_state_number %}
                    {{b_address.gst_state_number}} 
                {% endif %}
            </td>
            <td style="width: 50%;">
                <b>{{ _("Name") }}:&nbsp;
                {% if s_address.address_line1 %}
                        {{s_address.address_title}}
                    {% endif %}
                </b><br>
                <b>{{ _("Address") }}:</b>&nbsp; 
                    {% if s_address.address_line1 %} 
                        {{s_address.address_line1}}, 
                    {% endif %}
                <br>
                {% if s_address.address_line2 %} 
                    {{s_address.address_line2}}, 
                {% endif %}<br>
                {% if s_address.city %} 
                    {{s_address.city}} 
                {% endif %}
                {% if s_address.pincode %} 
                    -{{s_address.pincode}}, 
                {% endif %}
                {% if s_address.gst_state %} 
                    {{s_address.gst_state}}, 
                {% endif %}
                {% if s_address.country %} 
                    {{s_address.country}}. 
                {% endif %}<br>

                <b>M.No.:</b>&nbsp;
                {% if s_address.phone %}
                    {{s_address.phone}}
                {% endif %}<br>
                <b>Email:</b>&nbsp;
                {% if s_address.email_id %}
                    {{s_address.email_id}} 
                {% endif %}<br>
                <b>{{ _("GSTIN") }}:</b>&nbsp;
                {% if s_address.gstin %}
                    {{s_address.gstin}}
                {% endif %}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                <b>{{ _("State Code") }}:</b>&nbsp;
                {% if s_address.gst_state_number %}
                    {{s_address.gst_state_number}}
                {% endif %}
            </td>
        </tr>
    </table>
    <table>
        <tr>
            <td>
                Please supply the following goods subject to terms and conditions stated below.
            </td>
        </tr>
    </table>
    {% if disc_col|length %}
        <table style="margin-top: 2px;">
            <tr>
                <th><b>{{ _("Sr No.") }}</b></th>
                <th><b>{{ _("Item Name") }}</b></th>
                <th><b>{{ _("HSN") }}</b></th>
                <th><b>{{ _("Delivery Schedule") }}</b></th>
                <th><b>{{ _("Quantity") }}</b></th>
                <th><b>{{ _("Rate") }}</b></th>
                <th><b>{{ _("Discount (%)") }}</b></th>
                <th style="width:12%;"><b>{{ _("Amount") }}</b></th>
            </tr>
            {%- for item in doc.items -%}
            <tr>
                <td style="text-align: center;">{{ loop.index }}</td>
                <td>{{ item.description }}
                </td>
                <td>
                    {% if item.gst_hsn_code %}
                        {{ item.gst_hsn_code }}
                    {% endif %}
                </td>
                <td style="text-align: center;"> 
                    {% if item.schedule_date %}
                        {{ item.get_formatted("schedule_date") }}
                    {% endif %}
                </td>
                <td style="text-align: center;">
                    {{ item.qty }}&nbsp;{{ item.uom }}
                </td>
                <td style="text-align: right;">{{ item.get_formatted("rate") }}</td>
                <td style="text-align: center;">{% if item.discount_percentage %}{{ item.discount_percentage }}{% endif %}</td>
                <td style="text-align: right;width:12%;">{{ item.get_formatted("amount") }}</td>
            </tr>
            {%- endfor -%}
            <tr>
                <th></th>
                <th></th>
                <th></th>
                <th style="text-align: center;"><b>{{ _("Total") }}</b></th>
                <th></th>
                <th></th>
                <th></th>
                <th style="text-align: right;width:12%;"><b>{{ doc.get_formatted("total") }}</b></th>
            </tr>
        </table>
    {% else %}
        <table style="margin-top: 2px;">
            <tr>
                <th><b>{{ _("Sr No.") }}</b></th>
                <th><b>{{ _("Item Name") }}</b></th>
                <th><b>{{ _("HSN") }}</b></th>
                <th><b>{{ _("Delivery Schedule") }}</b></th>
                <th><b>{{ _("Quantity") }}</b></th>
                <th><b>{{ _("Rate") }}</b></th>

                <th style="width:12%;"><b>{{ _("Amount") }}</b></th>
            </tr>
            {%- for item in doc.items -%}
            <tr>
                <td style="text-align: center;">{{ loop.index }}</td>
                <td>{{ item.description }}
                </td>
                <td>
                    {% if item.gst_hsn_code %}
                        {{ item.gst_hsn_code }}
                    {% endif %}
                </td>
                <td style="text-align: center;"> 
                    {% if item.schedule_date %}
                        {{ item.get_formatted("schedule_date") }}
                    {% endif %}
                </td>
                <td style="text-align: center;">
                    {{ item.qty }}&nbsp;{{ item.uom }}
                </td>
                <td style="text-align: right;">{{ item.get_formatted("rate") }}</td>

                <td style="text-align: right;width:12%;">{{ item.get_formatted("amount") }}</td>
            </tr>
            {%- endfor -%}
            <tr>
                <th></th>
                <th></th>
                <th></th>
                <th style="text-align: center;"><b>{{ _("Total") }}</b></th>
                <th></th>
                <th></th>

                <th style="text-align: right;width:12%;"><b>{{ doc.get_formatted("total") }}</b></th>
            </tr>
        </table>
    {% endif %}

    <table style="margin-top: 2px;">
        <tr>
            <td rowspan ="{{ rowspan }}" style ="width:57%;">
                <b>Total Amount in Words:</b><br>{{ doc.in_words }}
            </td>
            <td style ="width:28%;" class ="text-right">
                <b>Total Amount:</b>
            </td>
            <td style ="width:11%;" class ="text-right">{{ doc.get_formatted("total") }}</td>
        </tr>
        {% if doc.additional_discount_percentage %}
            <tr>
                <td style ="width:28%;" class ="text-right"><b>Additional Disc. on {{doc.apply_discount_on}}: &nbsp;{{doc.additional_discount_percentage}}%</b></td>
                <td style ="width:11%;" class ="text-right"> -&nbsp;{{ doc.get_formatted("discount_amount")}}</td>
            </tr>
        {% endif %}
        <tr>
            <td style ="width:28%;" class ="text-right"><b>Taxable Value:</b></td>
            <td style ="width:11%;" class ="text-right"> &nbsp;
            {{ doc.get_formatted("net_total")}}</td>
        </tr>
        {%- for tax in doc.taxes -%}
        <tr>
            <td style ="width:28%;" class ="text-right">
                <b>{{ tax.description }}</b>
            </td>
            <td style ="width:11%;" class ="text-right">
                {{ tax.get_formatted("tax_amount") }}
            </td>
        </tr>
        {%- endfor -%}
        <tr>
            <td style ="width:57%;">

            </td>
            <td style ="width:28%;" class ="text-right"><b>Total:</b></td>
            <td style ="width:11%;" class ="text-right"> {{ doc.get_formatted("grand_total")}}</td>
        </tr>
        <tr>
            <td style ="width:57%;"></td>
            <td style ="width:28%;" class ="text-right"><b>Round Off Amount :</b></td>
            <td style ="width:11%;" class ="text-right"> {{ doc.get_formatted("rounding_adjustment")}}</td>
        </tr>
        <tr>
            <td style ="width:57%;">
                <b>Payment Terms:</b>
                {% set pt=doc.payment_schedule %}
                {{pt[0].payment_term}}
            </td>
            <td style ="width:28%;" class ="text-right"><b>Invoice Value :</b></td>
            <td style ="width:11%;" class ="text-right"> {{ doc.get_formatted("rounded_total")}}</td>
        </tr>
    </table>
    {% if doc.other_charges_calculation %}
    <div style="margin-top: -15px; margin-bottom: -15px;">{{ doc.other_charges_calculation }} </div>
    {% endif %}
    <table style="margin-top: 0px;">
        <tr>
            <td style ="width:70%;font-size: 8px;" rowspan ="">
                {% if doc.terms %}
                    {{ doc.terms }}
                {% endif %}                
            </td>
            <td style="font-size:10px; width: 30%"><br>
                <p class="text-center">For <b>
                {% if doc.company %}
                    {{doc.company}}
                {% endif %}</b><br></p><br><br><br><br><br><br>
                <p class ="text-center"><b>Authorised Signatory</b></p>
            </td>
        </tr>
    </table>
</div>
